body.script:
// Interactive controls
const densityFilter = document.getElementById('densityFilter');
const systemButtons = document.querySelectorAll('[data-system]');
const resetButton = document.getElementById('resetView');
const toggleGridButton = document.getElementById('toggleGrid');
const svg = document.getElementById('sensorMap');

// Density filtering
densityFilter.addEventListener('change', function() {
    const selectedDensity = this.value;
    const groups = svg.querySelectorAll('.sensor-group');

    groups.forEach(group => {
        if (selectedDensity === 'all') {
            group.classList.remove('dimmed');
        } else {
            if (group.classList.contains(selectedDensity)) {
                group.classList.remove('dimmed');
                group.classList.add('highlight');
            } else {
                group.classList.add('dimmed');
                group.classList.remove('highlight');
            }
        }
    });
});

// System focus buttons
systemButtons.forEach(button => {
    button.addEventListener('click', function() {
        const system = this.dataset.system;
        const groups = svg.querySelectorAll('.sensor-group');

        // Reset all buttons
        systemButtons.forEach(btn => btn.classList.remove('active'));
        this.classList.add('active');

        groups.forEach(group => {
            if (system === 'wing' && group.classList.contains('wing-structure')) {
                group.classList.remove('dimmed');
                group.classList.add('highlight');
            } else if (system === 'gear' && group.classList.contains('landing-gear')) {
                group.classList.remove('dimmed');
                group.classList.add('highlight');
            } else if (system === 'engine' && group.classList.contains('engine-mounts')) {
                group.classList.remove('dimmed');
                group.classList.add('highlight');
            } else if (system === 'fuselage' && group.classList.contains('fuselage')) {
                group.classList.remove('dimmed');
                group.classList.add('highlight');
            } else {
                group.classList.add('dimmed');
                group.classList.remove('highlight');
            }
        });
    });
});

// Reset view
resetButton.addEventListener('click', function() {
    const groups = svg.querySelectorAll('.sensor-group');
    groups.forEach(group => {
        group.classList.remove('dimmed', 'highlight');
    });

    systemButtons.forEach(btn => btn.classList.remove('active'));
    densityFilter.value = 'all';
});

// Toggle grid
let gridVisible = true;
toggleGridButton.addEventListener('click', function() {
    const grid = svg.getElementById('referenceGrid');
    if (gridVisible) {
        grid.style.display = 'none';
        this.textContent = 'Show Grid';
    } else {
        grid.style.display = 'block';
        this.textContent = 'Toggle Grid';
    }
    gridVisible = !gridVisible;
});

// Add hover effects for sensor groups
const sensorGroups = svg.querySelectorAll('.sensor-group');
sensorGroups.forEach(group => {
    group.style.cursor = 'pointer';

    group.addEventListener('mouseenter', function() {
        if (!this.classList.contains('dimmed')) {
            this.style.filter = 'drop-shadow(0 0 5px rgba(25, 118, 210, 0.8))';
        }
    });

    group.addEventListener('mouseleave', function() {
        this.style.filter = '';
    });
});
